mapper.select:
SELECT a.id AS id, a.create_id AS createId, a.create_time AS createTime, a.uri AS uri, a.time_number AS timeNumber, a.time_str AS timeStr, a.name AS name, a.category AS category, a.region AS region, a.ip AS ip, a.success_flag AS successFlag, a.error_msg AS errorMsg FROM sys_request a WHERE a.del_flag = FALSE
AND a.uri LIKE concat('%', #{dto.uri}, '%')
AND a.name LIKE concat('%', #{dto.name}, '%')
AND a.ip LIKE concat('%', #{dto.ip}, '%')
AND a.region LIKE concat('%', #{dto.region}, '%')
AND a.time_number >= #{dto.beginTimeNumber}
AND a.time_number <= #{dto.endTimeNumber}
AND a.create_time >= #{dto.beginCreateTime}
AND a.create_time <= #{dto.endCreateTime}
AND a.create_id = #{dto.createId}
AND a.category = #{dto.category}
AND a.success_flag = #{dto.successFlag}

SELECT COUNT(1) AS count,
(SUM(a.time_number) DIV COUNT(1)) AS avg
FROM sys_request a
WHERE a.del_flag = FALSE

    AND a.uri LIKE concat('%', #{dto.uri}, '%')


    AND a.name LIKE concat('%', #{dto.name}, '%')


    AND a.ip LIKE concat('%', #{dto.ip}, '%')


    AND a.region LIKE concat('%', #{dto.region}, '%')


    AND a.time_number >= #{dto.beginTimeNumber}


    AND a.time_number <= #{dto.endTimeNumber}


    AND a.create_time >= #{dto.beginCreateTime}


    AND a.create_time <= #{dto.endCreateTime}


    AND a.create_id = #{dto.createId}


    AND a.category = #{dto.category}


    AND a.success_flag = #{dto.successFlag}
